import React from "react";
import {useDispatch} from "react-redux";
import {isDoneSwitched} from "./ToDoTasksSlice";

export default function SwitchIsDoneButton(props){
    const id = props.id;

    const dispatch = useDispatch();

    const onDeleteTaskClicked = () => {
        if(id){
            dispatch(
                isDoneSwitched({
                    id: id
                })
            );
        }
    }

    return(
        <button className="setDoneButt" onClick={onDeleteTaskClicked}><span>{props.children}</span></button>
    )
}
