import {createSlice} from '@reduxjs/toolkit'


const initialState = [
    {
        id: "1",
        title: "Do something",
        categoryId: '2',
        category: "Work",
        createdDate: "2022-06-01T13:53",
        deadlineDate: "2022-06-23T13:53",
        isDone: false,
        doneDate: ""
    },
    {
        id: "8",
        title: "Do something 4",
        categoryId: '3',
        category: "School",
        createdDate: "2022-06-04T13:53",
        deadlineDate: "",
        isDone: true,
        doneDate: "2022-06-04T13:56"
    },
]

const toDoTasksSlice = createSlice({
    name: 'toDoTasks',
    initialState,
    reducers: {
        taskAdded(state, action) {
            state.push(action.payload);
        }
    }
})
export const {taskAdded} = toDoTasksSlice.actions;

export default toDoTasksSlice.reducer
