export default function DeleteTaskButton(props) {
    const dispatch = useDispatch();

    const root = document.querySelector("#root");
    const submit = () => {
        confirmAlert({
            title: 'Confirm to submit',
            message: 'Are you sure to do this.',
            buttons: [
                {
                    label: 'Yes',
