import React from "react";
import {useDispatch} from "react-redux";
import {taskDeleted} from "../ToDoTasksSlice";
import {confirmAlert} from "react-confirm-alert";

export default function DeleteTaskButton(props) {
    const dispatch = useDispatch();

    const onDeleteTaskClicked = () => {
        if (props.id) {
            dispatch(
                taskDeleted({
                    id: props.id
                })
            )
        }
    }

    const submit = () => {
        confirmAlert({
            title: 'Confirm to submit',
            message: 'Are you sure to do this.',
            buttons: [
                {
                    label: 'Yes',
                    onClick: () => alert('Click Yes')
                },
                {
                    label: 'No',
                    onClick: () => alert('Click No')
                }
            ]
        });
    };

    return (
        <button className="deleteButt" onClick={this.submit}><span role="img" aria-label="cross">‚ùå</span></button>
    )
}
