import React, {useState} from "react";
import {useSelector} from "react-redux";
import { useDispatch } from 'react-redux'
import { nanoid } from '@reduxjs/toolkit'

import { taskAdded } from './ToDoTasksSlice'


export function AddTaskForm() {
    const categories = useSelector(state => state.categories);

    const [title, setTitle] = useState('');
    const [categoryId, setCategoryId] = useState(0);
    const [deadline, setDeadline] = useState('');

    const dispatch = useDispatch();

    const onTitleChanged = e => setTitle(e.target.value);
    const onCategoryIdChanged = e => setCategoryId(e.target.value);
    const onDeadlineChanged = e => setDeadline(e.target.value);

    const onSaveTaskClicked = () => {
        if(title && categoryId &&  deadline){
            
        }
    }

    let categoriesCopy = [...categories];
    categoriesCopy = categoriesCopy.filter(category => category.id !== 1);


    return (
        <div className="addTask">
            <h3>Add task:</h3>
            <form method="post">
                <div>
                    <p>Title</p>
                    <input type="text" size="50" value={title} onChange={onTitleChanged}/>
                </div>
                <div>
                    <p>Category</p>
                    <select value={categoryId} onChange={onCategoryIdChanged}>
                        {categoriesCopy.map(category => (
                            <option key={category.id} value={category.id}>{category.name}</option>
                        ))}
                    </select>
                </div>
                <div>
                    <p>Deadline (empty - no deadline)</p>
                    <input type="datetime-local" value={deadline} onChange={onDeadlineChanged}/>
                </div>
                <input type="submit" value="Create"/>
            </form>
        </div>
    )
}
