import React, {useState} from "react";
import {useSelector} from "react-redux";

export function CategoryPicker(props) {
    const {setCategoryId, category} = props;
    const categories = useSelector(state => state.categories)
    const renderedOptions = categories.map(category => (
        <option key={category.id} value={category.id}>{category.name}</option>
    ));
    function handleCategoryInput(event){
        setLocalCategoryId(event.target.value);
    }
    return (
        <div className="categoryFilter">
            <form >
                <select name="categoryId" id="categoryId" value={category} onChange={() => handleCategoryInput(category)}>
                    <option value="">All</option>
                    {renderedOptions}
                </select>
            </form>
        </div>
    )
}
